mapper.update:
UPDATE TRIP
SET trip_name = #{name}, trip_start_date = #{startDate}, trip_end_date = #{endDate}
WHERE trip_no = #{no}

mapper.select:
SELECT trip_no , trip_name, route_no , day_no, theme_no, city_no FROM ( SELECT CEIL(ROWNUM / 16) request_page, t.trip_no , t.trip_name, s.route_no , s.day_no, r.theme_no, spot.city_no
FROM trip t, small_trip s, route_theme r, route, spot
t.trip_no = s.trip_no and r.trip_no = t.trip_no and s.route_no = route.route_no and route.spot_no = spot.spot_no
and r.theme_no = #{themeNo}
and city_no in
#{value}
) where request_page = #{page}

SELECT Max(CEIL(ROWNUM / 16)) FROM ( SELECT CEIL(ROWNUM / 16) request_page, t.trip_no , t.trip_name, s.route_no , s.day_no, r.theme_no, spot.city_no FROM trip t, small_trip s, route_theme r, route, spot
t.trip_no = s.trip_no and r.trip_no = t.trip_no and s.route_no = route.route_no and route.spot_no = spot.spot_no
and spot.city_no in
#{name}
)
